import { useEffect, useRef, useState } from 'react';
import Link from 'next/link';
import { useIns, useInsDispatch } from '../pages/contexts/CartContext';
import { useUser } from '../pages/contexts/userContext';
import { usefunctions } from '../pages/contexts/functionContext';
import styles from '../styles/cart.module.css';
import Image from 'next/image';
import { formatNumber } from '../utils/utils';

export default function CartInstances({ iteminstance }) {
    const inputEL = useRef(null);
    const checkEL = useRef(null);
    const [incdis, setIncdis] = useState(false);
    const [decdis, setDecdis] = useState(false);
    const [dltdis, setDltdis] = useState(false);
    const [price, setPrice] = useState(0);

    const user = useUser();
    const { ChangeToCart, DeleteIns, ChangeAddCart, ChgChk } = usefunctions();
    const { setInstance, setInsLen, setItemCount, setTotal } = useInsDispatch();
    const { instances } = useIns();

    //https://stackoverflow.com/questions/6649327/regex-to-remove-letters-symbols-except-numbers
    const handleCount = (e) => {
        var v = e.target.value.replace(/\D+/g, '').replace(/^0+/, '');
        inputEL.current.value = v;
        setIncdis(parseInt(inputEL.current.value) >= 999);
        setDecdis(parseInt(inputEL.current.value) <= 1);
    };

    const handleIncrease = (e) => {
        setIncdis(true);
        setDecdis(true);
        setDltdis(true);
        inputEL.current.value = parseInt(inputEL.current.value) + 1;
        setPrice(
            inputEL.current && inputEL.current.value * iteminstance.item.price
        );
        //hanle DB -> plus value
        ChangeAddCart(
            1,
            iteminstance._id,
            user,
            setInstance,
            setInsLen,
            setItemCount,
            setTotal
        ).then(() => {
            setIncdis(parseInt(inputEL.current.value) >= 999);
            setDecdis(parseInt(inputEL.current.value) <= 1);
            setDltdis(false);
        });
    };
    const handleDecrease = (e) => {
        setIncdis(true);
        setDecdis(true);
        setDltdis(true);
        inputEL.current.value = parseInt(inputEL.current.value) - 1;
        setPrice(
            inputEL.current && inputEL.current.value * iteminstance.item.price
        );
        //hanle DB -> minus value
        ChangeAddCart(
            -1,
            iteminstance._id,
            user,
            setInstance,
            setInsLen,
            setItemCount,
            setTotal
        ).then(() => {
            setIncdis(parseInt(inputEL.current.value) >= 999);
            setDecdis(parseInt(inputEL.current.value) <= 1);
            setDltdis(false);
        });
    };

    const handlefocus = () => {
        setIncdis(true);
        setDecdis(true);
        setDltdis(true);
        //handle DB -> change value

        !inputEL.current.value &&
            (inputEL.current.value = iteminstance.quantity);
        setPrice(
            inputEL.current && inputEL.current.value * iteminstance.item.price
        );
        ChangeToCart(
            inputEL.current.value,
            iteminstance._id,
            user,
            setInstance,
            setInsLen,
            setItemCount,
            setTotal
        ).then(() => {
            setIncdis(parseInt(inputEL.current.value) >= 999);
            setDecdis(parseInt(inputEL.current.value) <= 1);
            setDltdis(false);
        });
    };

    const handleDelIns = () => {
        DeleteIns(
            iteminstance._id,
            user,
            setInstance,
            setInsLen,
            setItemCount,
            setTotal
        );
    };

    const handleCheck = () => {
        console.log('checkEL.current.checked: ', checkEL.current.checked);
        ChgChk(
            checkEL.current.checked,
            iteminstance._id,
            user,
            setInstance,
            setInsLen,
            setItemCount,
            setTotal
        ).then(() => {
            console.log('chgchk complete');
            console.log('instances after ChgChk: ', instances);
        });
    };
    useEffect(() => {
        setIncdis(parseInt(inputEL.current.value) >= 999);
        setDecdis(parseInt(inputEL.current.value) <= 1);
        setPrice(
            inputEL.current && inputEL.current.value * iteminstance.item.price
        );
    }, [user]);
    return (
        <>
            <div key={iteminstance._id} className="row">
                <div className="col d-flex ">
                    <div>
                        <input
                            type="checkbox"
                            //value='true'
                            ref={checkEL}
                            defaultChecked={iteminstance.check}
                            onChange={handleCheck}
                        />
                    </div>
                    <div>
                        <Link
                            href="/store/[id]"
                            as={`store/${iteminstance.item._id}`}
                        >
                            <a className="btn btn-link btn-sm mr-2">
                                <Image
                                    src={`/${iteminstance.item.image}`}
                                    height={70}
                                    width={70}
                                />
                            </a>
                        </Link>
                    </div>
                    <div className="align-self-center mr-2">
                        <Link
                            href="/store/[id]"
                            as={`store/${iteminstance.item._id}`}
                        >
                            <a className="btn btn-link btn-sm  ">
                                {iteminstance.item.title}
                            </a>
                        </Link>
                    </div>

                    <div className=" align-self-center mr-2">
                        unit price: {formatNumber(iteminstance.item.price)}
                    </div>

                    <div className={styles.inputwraper1}>
                        <button
                            onClick={() => handleDecrease(iteminstance)}
                            className={styles.minus1}
                            disabled={decdis}
                        >
                            - minus
                        </button>
                        <input
                            type="text"
                            ref={inputEL}
                            onInput={handleCount}
                            onBlur={handlefocus}
                            //onChange={handleCount}
                            className={styles.input1}
                            defaultValue={iteminstance.quantity}
                        />
                        <button
                            onClick={() => handleIncrease(iteminstance)}
                            className={styles.add1}
                            disabled={incdis}
                        >
                            + add
                        </button>
                    </div>
                    <div className="align-self-center mr-3">
                        price: {formatNumber(price)}
                    </div>
                    <div className="align-self-center">
                        <button
                            onClick={() => handleDelIns(iteminstance)}
                            className="btn btn-outline-primary btn-sm disable "
                            disabled={dltdis}
                        >
                            Delete Item
                        </button>
                    </div>
                </div>
            </div>
        </>
    );
}
