import React, { createContext, useContext, useReducer } from 'react';

export const CartContext = createContext();
export const CartContextActionC = createContext();

const reducer = (state, action) => {
  switch (action.type) {
    case 'INCREASE': {
      fetch('/api/user_addtocart_post', {
        method: 'post',
        body: JSON.stringify({
          count: 1,
          itemid: action.payload._id,
          user: action.user,
        }),
      })
        .then((response) => response.json())
        .then((data) => {
          console.log('data: ', data);
        });
      return state + 1;
    }

    case 'DECREASE':
      return state - 1;
    case 'INCREASE_BY':
      return JSON.parse(JSON.stringify(action.payload));
    default:
      throw new Error(`Unknown action: ${action.type}`);
  }
};

const CartContextProvider = ({ children }) => {
  const [len, dispatch] = useReducer(reducer, 0);
  const [instances, setInstance] = useState('');
  return (
    <CartContext.Provider value={{ len, instances }}>
      <CartContextActionC.Provider value={{ dispatch, setInstance }}>
        {children}
      </CartContextActionC.Provider>
    </CartContext.Provider>
  );
};

export const useInslength = () => useContext(CartContext);
export const useInslenDispatch = () => useContext(CartContextActionC);
export default CartContextProvider;
