import React, { createContext, useContext, useReducer } from 'react';

export const ProductsContext = createContext();
export const ProductsContext1 = createContext();

const reducer = (state, action) => {
  switch (action.type) {
    case 'INCREASE':
      return state + 1;
    case 'DECREASE':
      return state - 1;
    case 'INCREASE_BY':
      return JSON.parse(JSON.stringify(action.payload));
    default:
      throw new Error(`Unknown action: ${action.type}`);
  }
};

const ProductsContextProvider = ({ children }) => {
  const [state, dispatch] = useReducer(reducer, 0);
  return (
    <ProductsContext.Provider value={state}>
      <ProductsContext1.Provider value={dispatch}>
        {children}
      </ProductsContext1.Provider>
    </ProductsContext.Provider>
  );
};

export const useItems = () => useContext(ProductsContext);
export const usedispatch = () => useContext(ProductsContext1);
export default ProductsContextProvider;
