import Layout from '../../../components/layout';
import { useRouter } from 'next/router';
import { useRef } from 'react';

import { usefunctions } from '../../contexts/functionContext';

export default function Comment() {
    const textEL = useRef(null);
    const imgEL = useRef(null);
    const { PostComment } = usefunctions();
    const router = useRouter();
    const ins = JSON.parse(router.query.ins);
    console.log('router: ', router);
    console.log('ins: ', ins);

    const handleSubmit = (e) => {
        e.preventDefault();
        // PostComment(ins._id, textEL.current.value);
        console.log('hello');
        console.log('imgEL: ', imgEL);
        console.log('file0: ', imgEL.current.files[0]);
        var file = imgEL.current.files[0];
        console.log('file: ', file);
        const formData = new FormData();
        formData.append('file', file);
        console.log('formdata: ', formData);
        //PostComment(ins._id, textEL.current.value, file);
    };

    const handleCancel = (e) => {
        e.preventDefault();
        console.log('cancel');
        router.push('/order');
    };

    const handleFile = () => {};
    return (
        <Layout>
            <div className="container mt-3 ">
                <div>
                    <img src={`/${ins.item.image}`} height={70} width={70} />
                </div>
                <div>{ins.item.title}</div>
                <form onSubmit={handleSubmit}>
                    <label>Comment:</label>
                    <textarea
                        ref={textEL}
                        className="form-control"
                        rows="5"
                        id="comment"
                    ></textarea>
                    <div className="custom-file">
                        <label className="custom-file-label">
                            <input
                                type="file"
                                className="file-control-file"
                                id="customFile"
                                onChange={handleFile}
                            />
                            Choose file
                        </label>
                    </div>
                    <button className="btn btn-success mr-3">submit</button>
                    <button
                        className="btn btn-success mr-3"
                        onClick={handleCancel}
                    >
                        cancel
                    </button>
                </form>
            </div>
        </Layout>
    );
}
