import Layout from '../components/layout';
import CartInstances from '../components/CartInstances';
import { useUser } from './contexts/userContext';
import { instances } from './contexts/CartContext';
import { setInstance } from './contexts/CartContext';
import { useEffect, useState } from 'react';

export default function Cart() {
  const i = 1;
  const [instances, setInstance] = useState('');

  useEffect(() => {
    // Update the document title using the browser API
    fetch('api/user_getallcart_post', {
      method: 'post',
      body: JSON.stringify({
        user: sessionStorage.getItem('key'),
      }),
    })
      .then((response) => response.json())
      .then((result) => {
        setInstance(result.data);
      })
      .catch(new Error());
  }, [i]);

  return (
    <Layout>
      {/* Create a card for each item */}
      <div className='card-columns'>
        {instances &&
          instances.map((iteminstance) => {
            return (
              <CartInstances
                iteminstance={iteminstance}
                key={iteminstance._id}
              />
            );
          })}
        {!instances && <div>No items</div>}
      </div>
    </Layout>
  );
}
