import Layout from '../components/layout';
import { useRouter } from 'next/router';
import { useRef, useState } from 'react';
import { useIns, useInsDispatch } from './contexts/CartContext';
import { usefunctions } from './contexts/functionContext';
import { useUser } from './contexts/userContext';
//import styles from '../../styles/comment.module.css';
//import { formatNumber } from '../../utils/utils';

function TempImg({ imgsrc }) {
    return <img src={imgsrc} width={50} height={50} className="mr-3 mb-3" />;
}
export default function Contact() {
    const [error, setError] = useState([]);
    const user = useUser();
    const textEL = useRef(null);
    const formEL = useRef(null);
    const { PostComment, GetAllOrder } = usefunctions();

    const { orders } = useIns();
    const { setOrders, setCmtmsg } = useInsDispatch();

    const router = useRouter();

    var img = [];
    const [imgsrcs, setImgsrcs] = useState([]);

    const handleSubmit = (e) => {
        e.preventDefault();
        setError('');
        const formData = new FormData(formEL.current);
        fetch('/api/owner_updateitem_post', {
            method: 'post',
            body: formData,
        }).then((res) => {
            if (res.status === 201) {
                console.log('I am in handlesubmit 201');
                // GetAllOrder(user, setOrders); //??

                //router.push(`/order/#${ins._id}`);
            } else {
                setError(res.error);
            }
        });
    };

    const handletmpimg = (e) => {
        for (let i = 0; i < e.target.files.length; i++) {
            img.push(URL.createObjectURL(e.target.files[i]));
        }
        setImgsrcs(img);
    };
    const handleCancel = (e) => {
        e.preventDefault();
        router.back();
    };

    return (
        <Layout>
            <div className="container mt-3">
                <hr />

                <form
                    ref={formEL}
                    onSubmit={handleSubmit}
                    method="post"
                    encType="multipart/form-data"
                >
                    {error && <p style={{ color: 'red' }}>{error}</p>}

                    <label>
                        title
                        <input name="title" />
                    </label>
                    <label>
                        owner
                        <input name="owner" />
                    </label>
                    <label>
                        quantity
                        <input name="quantity" />
                    </label>
                    <label>
                        price
                        <input name="price" />
                    </label>
                    <label>
                        introduction
                        <textarea
                            name="introduction"
                            ref={textEL}
                            className="form-control mb-3 mt-3"
                            rows="5"
                            // defaultValue={ins.commenttext}
                        ></textarea>
                    </label>

                    <div className="custom-file">
                        <label className="custom-file-label">
                            <input
                                onChange={handletmpimg}
                                name="itemattach"
                                type="file"
                                className="file-control-file"
                                //   onChange={handleFile}
                                multiple="multiple"
                            />
                            upload imge(s)
                        </label>
                    </div>
                    {imgsrcs.map((imgsrc) => (
                        <TempImg key={imgsrc} imgsrc={imgsrc} />
                    ))}

                    <button className="btn btn-success mr-3">submit</button>
                </form>
            </div>
        </Layout>
    );
}
