import Link from 'next/link';
import { useInsDispatch } from '../pages/contexts/CartContext';
import { useUser } from '../pages/contexts/userContext';
import { usefunctions } from '../pages/contexts/functionContext';

export default function CartInstances({ iteminstance }) {
  const user = useUser();
  const { AddToCart, GetAllCarts } = usefunctions();
  const { dispatch, setInstance, setLen } = useInsDispatch();

  const handleIncrease = (e) => {
    AddToCart(1, iteminstance._id, user)
      .then(() => GetAllCarts(user, setInstance))
      .then(() => {
        const reducer = (accumulator, currentValue) =>
          accumulator + currentValue.quantity;
        var inslen = instances ? instances.reduce(reducer, 0) : 0;
        setLen(inslen);
        console.log('instances in intensinindex: ', instances);
      });

    /*   dispatch({
      type: 'INCREASE_BY',
      payload: 1,
    });*/
  };
  const handleDecrease = (e) => {
    dispatch({
      type: 'DECREASE',
      payload: iteminstance,
      user: user,
    });
  };

  return (
    <>
      <div key={iteminstance._id}>
        <div className='card card-body'>
          <img
            src={iteminstance.item && iteminstance.item.image}
            style={{
              display: 'block',
              margin: '0 auto 10px',
              height: '200px',
              width: '200px',
            }}
          />
          <p className='item-name'>
            {iteminstance.item && iteminstance.item.title}
          </p>

          <h3 className='text-left'>
            price: {iteminstance.item && iteminstance.item.price}
          </h3>

          <div className='text-right'>
            <Link href='/[id]/detail' as={`/${iteminstance._id}/detail`}>
              <a className='btn btn-link btn-sm mr-2'>detail</a>
            </Link>

            <button
              onClick={() => handleIncrease(iteminstance)}
              className='btn btn-outline-primary btn-sm'
            >
              + add
            </button>
            <button
              onClick={() => handleDecrease(iteminstance)}
              className='btn btn-outline-primary btn-sm'
            >
              - minus
            </button>
            {/*isInCart(product) && (
                <button
                  onClick={() => increase(product)}
                  className='btn btn-outline-primary btn-sm'
                >
                  Add more
                </button>
              )*/}

            {/*!isInCart(product) && (
                <button
                  onClick={() => addProduct(product)}
                  className='btn btn-primary btn-sm'
                >
                  Add to cart
                </button>
              )*/}
          </div>
        </div>
      </div>
    </>
  );
}
