import Head from 'next/head';
import { useIns, useInsDispatch } from '../pages/contexts/CartContext';

import { useUser, useUserDispatch } from '../pages/contexts/userContext';

import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';

export default function Layout({
    children,
    title = 'This is the default title',
}) {
    const { inslen } = useIns();
    const { user } = useUser();
    const { setInstances } = useInsDispatch();
    const { setUser } = useUserDispatch();
    const handleLogout = () => {
        setUser('');
        setInstances('');
    };
    return (
        <div>
            <Head>
                <title>{title}</title>
                <meta charSet="utf-8" />
                <meta
                    name="viewport"
                    content="initial-scale=1.0, width=device-width"
                />
            </Head>
            <Navbar bg="light" expand="lg">
                <Navbar.Brand href="/">Home</Navbar.Brand>

                <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="mr-auto">
                        <Nav.Link href="/about">About</Nav.Link>
                        <Nav.Link href="/">Contact</Nav.Link>
                    </Nav>
                    <Nav>
                        {!user && <Nav.Link href="/logon">Login</Nav.Link>}
                        {user && (
                            <Nav.Link onClick={handleLogout}>LogOut</Nav.Link>
                        )}
                        <Nav.Link href="/cart">Cart ({inslen})</Nav.Link>
                        <Nav.Link href="/order">order</Nav.Link>
                    </Nav>
                </Navbar.Collapse>
            </Navbar>{' '}
            {children}
        </div>
    );
}
