import Link from 'next/link';
export default function ItemsInIndex({ item }) {
  const handleIncrease = (e) => {
    if (user) {
      var user = req.user;
    } else if (sessionStorage.getItem('key')) {
      user = sessionStorage.getItem('key');
    } else {
      user = Math.random();
      sessionStorage.setItem('key', user);
    }

    fetch('/api/user_addtocart_post', {
      method: 'post',
      body: {
        count: 2,
        itemid: item._id,
        user: user,
      },
    })
      .then((response) => response.json())
      .then((data) => {
        window.alert(data);
      });
  };
  return (
    <>
      <div key={item._id}>
        <div className='card card-body'>
          <img
            src={item.image}
            style={{
              display: 'block',
              margin: '0 auto 10px',
              height: '200px',
              width: '200px',
            }}
          />
          <p className='item-name'>{item.title}</p>

          <h3 className='text-left'>price: {item.price}</h3>

          <div className='text-right'>
            <Link href='/[id]/detail' as={`/${item._id}/detail`}>
              <a className='btn btn-link btn-sm mr-2'>detail</a>
            </Link>

            <button
              onClick={() => handleIncrease(item)}
              className='btn btn-outline-primary btn-sm'
            >
              Add more
            </button>
            {/*isInCart(product) && (
                <button
                  onClick={() => increase(product)}
                  className='btn btn-outline-primary btn-sm'
                >
                  Add more
                </button>
              )*/}

            {/*!isInCart(product) && (
                <button
                  onClick={() => addProduct(product)}
                  className='btn btn-primary btn-sm'
                >
                  Add to cart
                </button>
              )*/}
          </div>
        </div>
      </div>
    </>
  );
}
