import React, { createContext, useContext } from 'react';

export const FunctionContext = createContext();

const GetAllCarts = (parmsuser, howto) => {
  return new Promise((res, rej) =>
    fetch('api/user_getallcart_post', {
      method: 'post',
      body: JSON.stringify({
        user: parmsuser,
      }),
    })
      .then((response) => response.json())
      .then((result) => {
        howto(result.data);
        res();
      })
      .rej(new Error())
  );
};

const AddToCart = (parmscount, parmsid, parmsuser) => {
  console.log('parmsid: ', parmsid);
  fetch('/api/user_addtocart_post', {
    method: 'post',
    body: JSON.stringify({
      count: parmscount,
      itemid: parmsid,
      user: parmsuser,
    }),
  })
    .then((response) => response.json())
    .then((data) => {
      console.log('data: ', data);
    })
    .catch(new Error());
};

const FunctionContextProvider = ({ children }) => {
  return (
    <FunctionContext.Provider value={{ GetAllCarts, AddToCart }}>
      {children}
    </FunctionContext.Provider>
  );
};

export const usefunctions = () => useContext(FunctionContext);
export default FunctionContextProvider;
