import styles from './about/about.module.css';
import { useRef, useState } from 'react';
export default function About() {
    const divref = useRef(null);
    const imgref = useRef(null);
    const rsltref = useRef(null);
    const [hover, setHover] = useState(false);
    const handleHover = () => {
        setHover(true);
    };

    const handlestatic = () => {
        setHover(false);
    };
    return (
        <div>
            <div className="">
                <img
                    onMouseEnter={handleHover}
                    onMouseLeave={handlestatic}
                    id="myimage"
                    //  src="bedtime.icon"
                    src="/uploads/6d24b02bfe7e19cccb870933a2126816"
                    width="300"
                    height="240"
                    alt="Girl"
                    className={hover ? styles.imghover : ''}
                />
                <div id="myresult" className=""></div>
            </div>

            <div class="bg-image"></div>

            <div class="bg-text">
                <h1>I am John Doe</h1>
                <p>And I'm a Photographer</p>
            </div>
        </div>
    );
}
